import axios from "axios";

export function loadData(){
    return(dispatch)=>{
        return axios.get("https://covidnigeria.herokuapp.com/api").then((response)=>{
            dispatch(changeColor("#"+response.data.new_color));
        })
    }
}

export function changeColor(color){
    return{
        type:"CHANGE_COLOR",
        color:color
    }
}
