let defaultState={
    color:"red"
}

const mainReducer=(state=defaultState,action)=>{
    if(action.type==="SHOW_REUSLT"){
        return{
            ...state,
            result:action.result
        }
    } else{
        return{
            ...state
        }
    }
}

export default mainReducer;
